sp_widget.client_script:
function appdetail($rootScope, $location, $window, $timeout, $sce) {
    /* widget controller */
    var c = this;

    c.showVideo = false;
    $rootScope.userSelectedArtifact = '';
    c.searchObject = $location.search();

    c.tourVideo = document.getElementById("tourVideo");
    c.tourVideoMobile = document.getElementById("mobiletourVideo");
    $rootScope.startDetailDisplay = false;
    c.artifactsSimpleArray = [];
    c.appRepos = [];



    if (c.searchObject.sys_id && c.searchObject.artifact_type) {
        $rootScope.userSelectedArtifact = c.searchObject.artifact_type;
        getAppData(c.searchObject.sys_id);
        getArtifactDetails(c.searchObject.sys_id, c.searchObject.artifact_type);
        $rootScope.$broadcast('appChanged', c.searchObject);
        //  $rootScope.startDetailDisplay = true;
        // initializeStateSelector();

    } else if (c.searchObject.sys_id) {
        $rootScope.startDetailDisplay = false;
        getAppData(c.searchObject.sys_id);
    }


    $rootScope.$on('showAppDetail', function(event, data) {
        //$rootScope.startDetailDisplay = false;
        $rootScope.userSelectedArtifact = '';
        c.searchObject = $location.search();
        $rootScope.$broadcast('appChanged', c.searchObject);
        getAppData(data);
        // if (c.data.appData && c.data.appData.name.display_value) {
        //     $window.location.href = "/city?sys_id=" + c.searchObject.sys_id;
        // } else {
        //     getAppData(data);
        // }

        $rootScope.showDetail = true;

    });

    $rootScope.$on('showArtifactDetails', function(event, data) {
        $timeout(function() {
            $rootScope.userSelectedArtifact = data.artifact_type;
        });
        c.searchObject = $location.search();
        getAppData(data.sys_id);
        getArtifactDetails(data.sys_id, data.artifact_type);

        // $rootScope.showDetail = true;
    });

    c.hideDetail = function() {
        $rootScope.userSelectedArtifact = "";
        angular.element('.banner-encloser').scrollTop(0);
        $rootScope.startDetailDisplay = false;
        $rootScope.$broadcast('hideDetail', '');
    }

    function getAppData(sysID) {
        $rootScope.showDetail = true;
        c.server.get({ action: 'getAppData', sysid: sysID }).then(function(response) {
            console.log(response);
            $timeout(function() {
                c.data.appData = response.data.appData;
                c.data.appArtifacts = response.data.appArtifacts;
                c.centerImage = c.data.appData.screenshot.display_value;
                if ($rootScope.userSelectedArtifact) {
                    $rootScope.startDetailDisplay = true;
                    initializeStateSelector();
                }
            });

        });
    }

    function getArtifactDetails(sysID, artifactType) {

        c.server.get({ action: 'getArtifactDetails', sysid: sysID, artifact_type: artifactType }).then(function(response) {

            console.log('artifactdata');
            console.log(response);
            $timeout(function() {
                c.data.selectedArtifact = response.data.selectedArtifact;
                c.data.selectedArtifact.description.display_value = $sce.trustAsHtml(c.data.selectedArtifact.description.display_value);
                if ($rootScope.userSelectedArtifact == 'Installation') {
                    c.html = $sce.trustAsHtml(c.data.selectedArtifact.artifacts[0].html);
                    c.appRepos = response.data.selectedArtifact.appRepos;
                }
            });


        });
    }

    c.showAppTour = function(device) {
        if (device == 'desktop') {
            c.tourVideo.play();
            c.showVideo = true;
            c.showLive = false;
        } else {
            c.tourVideoMobile.play();
            c.showVideo = true;
            c.showLive = false;
        }


    }

    c.selectArtifact = function(artifact, index) {
        ///city?sys_id={{eachApp.sys_id}}&artifact_type={{eachartifact.value}}
        console.log();
        c.state = index + 1;
        $location.url("/city?sys_id=" + c.searchObject.sys_id + "&artifact_type=" + artifact.value);
        $location.url
    };

    $rootScope.$on('selectTheAppFromSideNav', function(event, data) {
        //debugger;
        c.selectArtifact(data.artifact, data.index);

    });
    c.learnToBuild = function() {
        $rootScope.startDetailDisplay = true;
        initializeStateSelector();
    }
    c.nextState = function() {
        //debugger;
        angular.element('.banner-encloser').scrollTop(0);
        $location.url("/city?sys_id=" + c.searchObject.sys_id + "&artifact_type=" + c.stateTracker[++c.state].value);
    }

    c.previousState = function() {
        angular.element('.banner-encloser').scrollTop(0);
        if (c.state > 1) {
            $location.url("/city?sys_id=" + c.searchObject.sys_id + "&artifact_type=" + c.stateTracker[--c.state].value);
        } else if (c.state == 1) {
            c.state = 0;
            $rootScope.userSelectedArtifact = "";
            $rootScope.startDetailDisplay = true;
            $location.url("/city?sys_id=" + c.searchObject.sys_id);

        }


    }

    function initializeStateSelector() {

        c.currentArtifact = [];
        c.stateTracker = [];
        for (var i = 0; i < c.data.appArtifacts.length; i++) {
            if (c.data.appArtifacts[i].sys_id == c.searchObject.sys_id) {
                c.currentArtifact = c.data.appArtifacts[i].artifacts;
                angular.copy(c.data.appArtifacts[i].artifacts, c.stateTracker);
            }
        }
        var learnToBuildObj = {
            'gradient1': '#4780CE',
            'gradient2': '#4780CE',
            'label': 'Learn to build',
            'value': 'Learn to build'

        }
        c.stateTracker.splice(0, 0, learnToBuildObj);
        if ($rootScope.userSelectedArtifact) {
            for (var j = 0; j < c.stateTracker.length; j++) {
                if (c.stateTracker[j].value == $rootScope.userSelectedArtifact) {
                    c.state = j;
                }
            }
        } else {
            c.state = 0;
        }
    };


    c.showLiveApp = function(device) {
        if (device == 'desktop') {
            c.tourVideo.pause();
            c.showLive = true;
            c.showVideo = false;

        } else {
            c.tourVideoMobile.pause();
            c.showVideo = false;

            window.open(c.data.appData.path.display_value, '_blank');
            win.focus();
        }

    }

    c.startMobileSlideShow = function() {
        c.tourVideo.pause();
        c.slideIndex = 0;
        c.developSlideShow = true;
        c.showDevDetails(c.mobileSlideShow[c.slideIndex].type, c.mobileSlideShow[c.slideIndex].devtype);
    }

}

