            var artifactGr = new GlideRecord('x_snc_city_app_artifacts_types');
            artifactGr.addQuery('app', input.sysid);
            artifactGr.addQuery('type', input.artifact_type);
            artifactGr.orderBy('order');
            artifactGr.query();
            while (artifactGr.next()) {
                var obj = {};
                $sp.getRecordElements(obj, artifactGr, 'sys_id,type,heading,short_description,description,gradient_start,gradient_end');
                obj.artifacts = [];
                obj.artifacts = getArtifactsForType(obj.sys_id.display_value, input.sysid);
                obj.learnMore = getLearnMoreLinks(obj.sys_id.display_value, input.sysid);
                if (input.artifact_type == 'Installation') {
                    obj.appRepos = [];
