    function getArtifactsForType(typeSysID, appSysID) {
        var tempObj = [];
        var artifactGr = new GlideRecord('x_snc_city_app_artifact');
        artifactGr.addQuery('app', appSysID);
        artifactGr.addQuery('type', typeSysID);
        artifactGr.addQuery('active', true);
        artifactGr.orderBy('order');
        artifactGr.query();
        while (artifactGr.next()) {
            var obj = {};
            $sp.getRecordElements(obj, artifactGr, 'sys_id,type,heading,text,app_experience,dev_experience,order,coupled_image_caption,integrated_app_icon,flip_image_sequence,display_layout');
            if (obj.type.display_value == 'Installation') {
                obj.html = artifactGr.installation_details.text.getDisplayValue();
                // return obj;
            }
            tempObj.push(obj);
        }
        return tempObj;
    };

    function getLearnMoreLinks(typeSysID) {
        var tempObj = [];
        var artifactGr = new GlideRecord('x_snc_city_learn_more_links');
        artifactGr.addQuery('app_artifact_type', typeSysID);
        artifactGr.orderBy('order');
        artifactGr.query();
        while (artifactGr.next()) {
            var obj = {};
            $sp.getRecordElements(obj, artifactGr, 'sys_id,display_text,url');
            tempObj.push(obj);
        }
        return tempObj;
    };

    function getArtifactsList(appObj) {
        var sysChoice = new GlideRecord('sys_choice');
        sysChoice.addQuery('name', 'x_snc_city_app_artifacts_types');
        sysChoice.addQuery('inactive', false);
        sysChoice.addQuery('element', 'type');
        sysChoice.orderBy('sequence');

        sysChoice.query();
        console.log(sysChoice.getRowCount());
        while (sysChoice.next()) {
            var obj = {};
            //$sp.getRecordElements(obj, appgr, 'label,value,sys_id');
            obj.value = sysChoice.value.toString();
            obj.label = sysChoice.label.toString();
            obj.sys_id = sysChoice.sys_id.toString();
            var gradientObj = getGradients(obj.value);
            obj.gradient1 = gradientObj.gradient1;
            obj.gradient2 = gradientObj.gradient2;
            appObj.artifacts.push(obj);
        }
    }

    function getGradients(type) {
        var artifactGr = new GlideRecord('x_snc_city_app_artifacts_types');
        artifactGr.addQuery('type', type);
        artifactGr.query();
        if (artifactGr.next()) {
            var obj = {};
            obj.gradient1 = artifactGr.gradient_start.toString();
            obj.gradient2 = artifactGr.gradient_end.toString();
            return obj;
        }
    }
})();
